import React, { useContext } from 'react';
import { Button, Col, Row } from 'react-bootstrap';
import {useNavigate} from 'react-router-dom'
import { Context } from '..';
import { deleteNote, updateNote } from '../http/noteAPI';
import { NOTE_ROUTE } from '../utils/consts';

const Note = ({notka}) => {

    const navigate = useNavigate()
    const {note} = useContext(Context)

    const onRemove = async () => {
        await deleteNote(notka._id)
        note.removeNote(notka._id)
    }

    const onComplete = async () => {
        // note.updateNote({
        //     ...notka,
        //     notka.isCompleted: !isCompleted
        // })
        let uNote = {
            ...notka,
            isCompleted: !notka.isCompleted
        }

        console.log(uNote)
        // await updateNote(uNote).then(data => console.log(data))
    }

    return (
        <Row 
            style={{cursor: "pointer"}}
        >
            <Col md={8}
                onClick={() => navigate(NOTE_ROUTE + '/' + notka._id)}
            >
                {notka.title}
            </Col>
            <Col md={2}>
                04:20 05.01 {note.isCompleted}
            </Col>
            <Col md={2}
                className='spa'
            >
                <Button
                    variant="outline-success"
                    onClick={() => onComplete(notka._id)}
                >
                    âœ“
                </Button>
                <Button
                    variant="outline-danger"
                    onClick={onRemove}
                >
                    &times;
                </Button>
            </Col>
        </Row>
    );
};

export default Note;
