import React, { useContext } from 'react';
import {Navbar, Container, Button, Nav} from 'react-bootstrap'
import { NavLink, useNavigate } from 'react-router-dom';
import { Context } from '..';
import { ADMIN_ROUTE, LOGIN_ROUTE, MAIN_ROUTE, TO_DO_ROUTE, TO_BUY_ROUTE, DONE_ROUTE } from '../utils/consts';
import {observer} from 'mobx-react-lite'

const NavBar = observer ( () => {

    const {user} = useContext(Context)
    const navigate = useNavigate()

    return (
        // <Navbar bg="dark" variant="dark">
        //     <Container>
        //         <Navbar.Brand href="#home">Navbar</Navbar.Brand>
        //         <Nav className="me-auto">
        //             <Nav.Link href="#home">Home</Nav.Link>
        //             <Nav.Link href="#features">Features</Nav.Link>
        //             <Nav.Link href="#pricing">Pricing</Nav.Link>
        //         </Nav>
        //     </Container>
        // </Navbar>
        <Navbar bg="dark" variant="dark">
            <Container>
                <Navbar.Brand href="#home">
                    <NavLink 
                        style={{color: 'white'}}
                        to={MAIN_ROUTE}
                    >
                        {user.isAuth ? 
                            <span>{user.user.username}</span>
                            : 'MyNotes'
                        }
                    </NavLink>
                </Navbar.Brand>
                {/* <NavLink 
                    style={{color: 'white'}}
                    to={MAIN_ROUTE}
                >
                    {user.isAuth ? 
                        <span>{user.user.username}</span>
                        : 'MyNotes'
                    }
                </NavLink> */}
                {user.isAuth ?
                    // <Nav className="me-auto">
                    //     <Nav.Link href="#home">Home</Nav.Link>
                    //     <Nav.Link href="#features">Features</Nav.Link>
                    //     <Nav.Link href="#pricing">Pricing</Nav.Link>
                    // </Nav>
                    <Nav.Link styled={{color: white}} href="#home">
                        Home
                    </Nav.Link>
                    // <Nav>
                    //     <Button variant={'outline-light'}
                    //         onClick={() => navigate(TO_DO_ROUTE)}
                    //     >
                    //         toDo
                    //     </Button>
                    //     <Button variant={'outline-light'}
                    //         onClick={() => navigate(TO_BUY_ROUTE)}
                    //     >
                    //         toBuy
                    //     </Button>
                    //     <Button variant={'outline-light'}
                    //         onClick={() => navigate(DONE_ROUTE)}
                    //     >
                    //         done
                    //     </Button>
                    // </Nav>
                    : <Nav></Nav>
                }

                {user.isAuth ?
                    <Nav className="ml-auto">
                        <Button variant={'outline-light'}
                            onClick={() => {
                                user.setIsAuth(false)
                                navigate(LOGIN_ROUTE)
                                localStorage.removeItem('token')
                            }}
                            className="mr-2"
                        >
                            Выйти
                        </Button>
                        <Button variant={'outline-light'}
                            // onClick={() => {}}
                        >
                            <NavLink 
                                to={ADMIN_ROUTE}
                                style={{color: 'white'}}
                            >
                                Админка
                            </NavLink>
                        </Button>
                    </Nav>
                    :
                    <Nav>
                        <Button variant={'outline-light'}
                            onClick={() => {
                                navigate(LOGIN_ROUTE)
                            }}
                        >
                            Войти
                        </Button>
                    </Nav>
                }

            </Container>
        </Navbar>
    );
});

export default NavBar;
