import { observer } from 'mobx-react-lite';
import React, { useContext } from 'react';
import { Button, Col, ListGroup, Row } from 'react-bootstrap';
import {useNavigate} from 'react-router-dom'
import { Context } from '..';
import { updateNote } from '../http/noteAPI';
import { NOTE_ROUTE } from '../utils/consts';

const Note = observer( ({notka}) => {

    const navigate = useNavigate()
    const {note} = useContext(Context)
    const {alert} = useContext(Context)

    const showModal = () => {
        alert.setModalVisible(true)
        alert.setModalNoteId(notka._id)
    }

    const onComplete = async () => {
        notka.isCompleted = !notka.isCompleted
        await updateNote(notka)
        // note.toggleNote(notka._id)
    }

    return (
        <ListGroup.Item 
            style={{cursor: "pointer"}}
            className="notka"
        >
            <Row>
                <Col md={8}
                    onClick={() => navigate(NOTE_ROUTE + '/' + notka._id)}
                >
                    {notka.title}
                </Col>
                <Col md={2}>
                    05.01.1986
                </Col>
                <Col md={2}
                >
                    <Button
                        variant="outline-success"
                        onClick={() => onComplete(notka._id)}
                        className="ms-5"
                    >
                        {notka.isCompleted ? `↩` : '✓'}
                    </Button>
                    <Button
                        variant="outline-danger"
                        onClick={showModal}
                        className="ms-4"
                    >
                        &times;
                    </Button>
                </Col>
            </Row>
        </ListGroup.Item>
    );
});

export default Note;
