import {$authHost, $host} from './index'

export const createNote = async (note) => {
    const response = await $authHost.post('api/note/create', note)
    return response
}

export const fetchNotes = async () => {
    const response = await $host.get('api/note/list')
    return response.data
}

export const deleteNote = async (id) => {
    const response = await $authHost.delete(`api/note/${id}`)
    return response.data
}

export const updateNote = async (note) => {
    const updatedNote = await $authHost.put('api/note/update', note._id, note)
}
