import React, { useState } from 'react';
import {Form} from 'react-bootstrap'
import { Context } from '..';
import { createNote } from '../http/noteAPI';

const Forma = () => {

    const {user} = useContext(Context)

    const [noteTitle, setNoteTitle] = useState('')

    // const click = async (event) => {
    //     event.preventDefault()
    //     try {
    //         let newNote
    //         newNote.title = noteTitle
    //         // data = await createNote(noteTitle)

    //     } catch(e) {
    //         alert(e.response.data)
    //     }
    // }

    const click = (event) => {
        event.preventDefault()
        const formData = new FormData()
        formData.append('title', noteTitle)
        formData.append('author', user.user.username)

        console.log(formData)
    }

    return (
        <Form className="mt-2" onSubmit={click}>
            <Form.Group className="mb-3" value={noteTitle} onChange={e => {
                setNoteTitle(e.target.value)
            }}>
                <Form.Control placeholder="Add new note" />
            </Form.Group>
        </Form>
    );
};

export default Forma;
