import { makeAutoObservable } from "mobx";

export default class AlertStore {
    constructor() {
        this._visible = false
        this._type = ""
        this._text = ""
        makeAutoObservable(this)
    }
    // setType(type) {
    //     this._type = type
    // }
    // setText(text) {
    //     this._text = text
    // }
    // setVisible(value) {
    //     this._visible = value
    // }
    showAlert(text, type) {
        this._type = type
        this._text = text
        this._visible = true
    }
    get type() {
        return this._type
    }
    get text() {
        return this._text
    }
    get visible() {
        return this._visible
    }
}
