import React, {useState, useContext} from 'react';
import {Alert, Button} from 'react-bootstrap'

import { Context } from '..';

const AlertBlock = () => {

    const [show, setShow] = useState(true)

    const {alert} = useContext(Context)

    if (alert.visible) {
        return (
          <Alert variant="danger" onClose={() => alert.setVisible(false)} dismissible>
            <Alert.Heading>{alert.text}111</Alert.Heading>
          </Alert>
        );
      }
      return <></>
};

export default AlertBlock;
